import React, { useState } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { Plus } from 'lucide-react';

const ExpenseForm = () => {
  const [inputState, setInputState] = useState({
    title: '',
    amount: '',
    date: '',
    category: '',
    description: '',
  });
  const [error, setError] = useState('');

  const { title, amount, date, category, description } = inputState;

  const handleInput = (name) => (e) => {
    setInputState({ ...inputState, [name]: e.target.value });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Mock addExpense function
      // await addExpense({
      //   ...inputState,
      //   amount: Number(amount),
      // });
      setInputState({
        title: '',
        amount: '',
        date: '',
        category: '',
        description: '',
      });
    } catch (error) {
      console.error('Error adding expense:', error);
      setError('Failed to add expense');
    }
  };

  return (
    <Card className="w-full max-w-lg mx-auto">
      <CardContent className="p-6">
        <form onSubmit={handleSubmit} className="space-y-6">
          {error && (
            <p className="text-sm text-red-500 mb-4">{error}</p>
          )}

          <div className="space-y-2">
            <Input
              type="text"
              value={title}
              onChange={handleInput('title')}
              placeholder="Expense Title"
              className="w-full"
            />
          </div>

          <div className="space-y-2">
            <Input
              type="number"
              value={amount}
              onChange={handleInput('amount')}
              placeholder="Expense Amount"
              className="w-full"
            />
          </div>

          <div className="space-y-2">
            <Input
              type="date"
              value={date}
              onChange={handleInput('date')}
              className="w-full"
            />
          </div>

          <div className="space-y-2">
            <Select value={category} onValueChange={(value) => setInputState({ ...inputState, category: value })}>
              <SelectTrigger className="w-full">
                <SelectValue placeholder="Select Category" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="education">Education</SelectItem>
                <SelectItem value="groceries">Groceries</SelectItem>
                <SelectItem value="health">Health</SelectItem>
                <SelectItem value="subscriptions">Subscriptions</SelectItem>
                <SelectItem value="takeaways">Takeaways</SelectItem>
                <SelectItem value="clothing">Clothing</SelectItem>
                <SelectItem value="travelling">Travelling</SelectItem>
                <SelectItem value="other">Other</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Textarea
              value={description}
              onChange={handleInput('description')}
              placeholder="Add A Reference"
              className="w-full min-h-[100px]"
            />
          </div>

          <div className="flex justify-center">
            <Button 
              type="submit"
              className="w-full max-w-xs bg-blue-600 hover:bg-blue-700 text-white"
            >
              <Plus className="w-4 h-4 mr-2" />
              Add Expense
            </Button>
          </div>
        </form>
      </CardContent>
    </Card>
  );
};

export default ExpenseForm;
