import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { signout } from '../../utils/Icons';
import { menuItems } from '../../utils/menuItems';
import { useGlobalContext } from '../../context/globalContext';

function Navigation({ active, setActive }) {
    const { token, setToken, userId, setUserId } = useGlobalContext(); 
    const [lastName, setLastName] = useState(localStorage.getItem('lastName')); 
    const [profileImage, setProfileImage] = useState(localStorage.getItem('profileImage')); // State for profile picture

    // Update the state when localStorage changes
    useEffect(() => {
        const storedLastName = localStorage.getItem('lastName');
        const storedProfileImage = localStorage.getItem('profileImage'); // Get the profile image from localStorage
        if (storedLastName) {
            setLastName(storedLastName);
        }
        if (storedProfileImage) {
            setProfileImage(storedProfileImage);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('lastName');
        localStorage.removeItem('profileImage'); // Remove profile image on logout
        setToken(null);  // Clear token in the global context
        setUserId(null); // Clear userId if you're using it in context
        window.location.href = '/login';
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setProfileImage(reader.result); // Update the profile image state
                localStorage.setItem('profileImage', reader.result); // Store the new image in localStorage
            };
            reader.readAsDataURL(file); // Convert the image to a base64 string
        }
    };

    return (
        <NavStyled>
            <div className="user-con">
                <div className="image-container">
                    {/* Display the profile image */}
                    <img 
                        src={profileImage || 'default-image-url'} // Placeholder image if no image is available
                        alt="Profile" 
                    />
                    {/* File input to allow image upload */}
                    <input 
                        type="file" 
                        accept="image/*" 
                        onChange={handleImageChange} 
                    />
                </div>
                <div className="text">
                    <h2>{lastName ? lastName : 'User'}</h2>
                    <p>Your Money</p>
                </div>
            </div>
            <ul className="menu-items">
                {menuItems.map((item) => (
                    <li key={item.id} onClick={() => setActive(item.id)} className={active === item.id ? 'active' : ''}>
                        {item.icon}
                        <span>{item.title}</span>
                    </li>
                ))}
            </ul>
            <div className="bottom-nav">
                <li onClick={handleLogout}>
                    {signout} Sign Out
                </li>
            </div>
        </NavStyled>
    );
}

const NavStyled = styled.nav`
    padding: 2rem 1.5rem;
    width: 374px;
    height: 100%;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: 2rem;

    .user-con {
        height: 100px;
        display: flex;
        align-items: center;
        gap: 1rem;

        .image-container {
            position: relative;
            img {
                width: 80px;
                height: 80px;
                border-radius: 50%;
                object-fit: cover;
                background: #fcf6f9;
                border: 2px solid #FFFFFF;
                padding: .2rem;
                box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
            }
            input {
                position: absolute;
                width: 100%;
                height: 100%;
                opacity: 0;
                top: 0;
                left: 0;
                cursor: pointer;
            }
        }

        h2 {
            color: rgba(34, 34, 96, 1);
        }
        p {
            color: rgba(34, 34, 96, .6);
        }
    }

    .menu-items {
        flex: 1;
        display: flex;
        flex-direction: column;
        li {
            display: grid;
            grid-template-columns: 40px auto;
            align-items: center;
            margin: .6rem 0;
            font-weight: 500;
            cursor: pointer;
            transition: all .4s ease-in-out;
            color: rgba(34, 34, 96, .6);
            padding-left: 1rem;
            position: relative;
            i {
                color: rgba(34, 34, 96, 0.6);
                font-size: 1.4rem;
                transition: all .4s ease-in-out;
            }
        }
    }

    .active {
        color: rgba(34, 34, 96, 1) !important;
        i {
            color: rgba(34, 34, 96, 1) !important;
        }
        &::before {
            content: "";
            position: absolute;
            left: 0;
            top: 0;
            width: 4px;
            height: 100%;
            background: #222260;
            border-radius: 0 10px 10px 0;
        }
    }
`;

export default Navigation;
