import React, { useContext, useState, useEffect } from "react";
import axios from 'axios';

const BASE_URL = "https://full-stact-expense-tracker.onrender.com/api/v1/";

const GlobalContext = React.createContext();

// Secure token storage utility
const TokenService = {
  getToken: () => {
    return sessionStorage.getItem('token'); // Using sessionStorage instead of localStorage
  },
  setToken: (token) => {
    if (token) {
      sessionStorage.setItem('token', token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    } else {
      sessionStorage.removeItem('token');
      delete axios.defaults.headers.common['Authorization'];
    }
  },
  removeToken: () => {
    sessionStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
  }
};

export const GlobalProvider = ({ children }) => {
    const [incomes, setIncomes] = useState([]);
    const [expenses, setExpenses] = useState([]);
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [userId, setUserId] = useState(null);

    // Initialize authentication state
    useEffect(() => {
        const token = TokenService.getToken();
        if (token) {
            setIsAuthenticated(true);
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            initializeData();
        }
    }, []);

    // Axios interceptors for handling auth errors
    useEffect(() => {
        const requestInterceptor = axios.interceptors.request.use(
            config => {
                setIsLoading(true);
                setError(null);
                return config;
            },
            error => {
                setIsLoading(false);
                return Promise.reject(error);
            }
        );

        const responseInterceptor = axios.interceptors.response.use(
            response => {
                setIsLoading(false);
                return response;
            },
            error => {
                setIsLoading(false);
                if (error.response?.status === 401) {
                    handleLogout();
                    setError('Session expired. Please login again.');
                } else if (error.response?.status === 403) {
                    setError('Access denied. You don\'t have permission for this action.');
                } else {
                    setError(error.response?.data?.message || 'An error occurred');
                }
                return Promise.reject(error);
            }
        );

        return () => {
            axios.interceptors.request.eject(requestInterceptor);
            axios.interceptors.response.eject(responseInterceptor);
        };
    }, []);

    const initializeData = async () => {
        try {
            await Promise.all([getIncomes(), getExpenses()]);
        } catch (error) {
            console.error('Failed to initialize data:', error);
        }
    };

    const handleLogin = (token, uid) => {
        TokenService.setToken(token);
        setIsAuthenticated(true);
        setUserId(uid);
        initializeData();
    };

    const handleLogout = () => {
        TokenService.removeToken();
        setIsAuthenticated(false);
        setUserId(null);
        setIncomes([]);
        setExpenses([]);
    };

    // Protected API request wrapper
    const protectedRequest = async (request) => {
        if (!isAuthenticated) {
            throw new Error('User not authenticated');
        }
        try {
            return await request();
        } catch (error) {
            if (error.response?.status === 401) {
                handleLogout();
            }
            throw error;
        }
    };

    const addIncome = async (income) => {
        await protectedRequest(() => 
            axios.post(`${BASE_URL}add-income`, income)
        );
        await getIncomes();
    };

    const getIncomes = async () => {
        const response = await protectedRequest(() => 
            axios.get(`${BASE_URL}get-incomes`)
        );
        setIncomes(response.data);
    };

    const deleteIncome = async (id) => {
        await protectedRequest(() => 
            axios.delete(`${BASE_URL}delete-income/${id}`)
        );
        await getIncomes();
    };

    const addExpense = async (expense) => {
        await protectedRequest(() => 
            axios.post(`${BASE_URL}add-expense`, expense)
        );
        await getExpenses();
    };

    const getExpenses = async () => {
        const response = await protectedRequest(() => 
            axios.get(`${BASE_URL}get-expenses`)
        );
        setExpenses(response.data);
    };

    const deleteExpense = async (id) => {
        await protectedRequest(() => 
            axios.delete(`${BASE_URL}delete-expense/${id}`)
        );
        await getExpenses();
    };

    // Calculation methods remain the same
    const totalIncome = () => incomes.reduce((total, income) => total + income.amount, 0);
    const totalExpenses = () => expenses.reduce((total, expense) => total + expense.amount, 0);
    const totalBalance = () => totalIncome() - totalExpenses();
    const transactionHistory = () => {
        const history = [...incomes, ...expenses];
        history.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        return history.slice(0, 4);
    };

    return (
        <GlobalContext.Provider value={{
            addIncome,
            getIncomes,
            incomes,
            deleteIncome,
            expenses,
            totalIncome,
            addExpense,
            getExpenses,
            deleteExpense,
            totalExpenses,
            totalBalance,
            transactionHistory,
            error,
            isLoading,
            isAuthenticated,
            handleLogin,
            handleLogout,
            userId
        }}>
            {children}
        </GlobalContext.Provider>
    );
};

export const useGlobalContext = () => {
    return useContext(GlobalContext);
};
