import React from 'react';
import {
    Chart as ChartJs,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { dateFormat } from '../../utils/dateFormat';

// Register necessary components from Chart.js
ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
);

function Chart() {
    const { incomes, expenses } = useGlobalContext();

    // Preparing the data for the chart
    const data = {
        labels: incomes.map((inc) => dateFormat(inc.date)), // Format dates for the x-axis
        datasets: [
            {
                label: 'Income',
                data: incomes.map((income) => income.amount), // Get income amounts
                backgroundColor: 'rgba(0, 128, 0, 0.5)', // Semi-transparent green
                borderColor: 'green',
                borderWidth: 2,
                tension: 0.2,
                fill: true, // Fill area under the line
            },
            {
                label: 'Expenses',
                data: expenses.map((expense) => expense.amount), // Get expense amounts
                backgroundColor: 'rgba(255, 0, 0, 0.5)', // Semi-transparent red
                borderColor: 'red',
                borderWidth: 2,
                tension: 0.2,
                fill: true, // Fill area under the line
            }
        ]
    };

    return (
        <ChartStyled>
            <Line data={data} options={{
                responsive: true,
                plugins: {
                    legend: {
                        display: true,
                        position: 'top',
                    },
                    tooltip: {
                        mode: 'index', // Show tooltip for all datasets at the same x-axis value
                        intersect: false,
                    },
                },
                scales: {
                    x: {
                        title: {
                            display: true,
                            text: 'Date',
                        },
                    },
                    y: {
                        title: {
                            display: true,
                            text: 'Amount',
                        },
                        beginAtZero: true, // Start y-axis from 0
                    },
                },
            }} />
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
`;

export default Chart;
