import React from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../context/globalContext';
import jsPDF from 'jspdf';
import 'jspdf-autotable'; // Import the autoTable plugin for jsPDF

const TransactionView = () => {
    const { incomes, expenses } = useGlobalContext();

    // Combine incomes and expenses
    const transactions = [...incomes, ...expenses].sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

    const downloadPDF = () => {
        const doc = new jsPDF();

        // Set title
        doc.text('All Transactions', 14, 16);

        // Create an array of data for the autoTable
        const tableData = transactions.map(transaction => {
            return [
                new Date(transaction.createdAt).toLocaleDateString(),
                transaction.description || transaction.title,
                transaction.amount,
                transaction.amount > 0 ? 'Income' : 'Expense'
            ];
        });

        // Add the table
        doc.autoTable({
            head: [['Date', 'Description', 'Amount', 'Type']],
            body: tableData,
            startY: 20, // Start the table below the title
            theme: 'grid', // Optional: change theme
        });

        // Save the PDF
        doc.save('transactions.pdf');
    };

    return (
        <TransactionViewStyled>
            <h2>All Transactions</h2>
            <button onClick={downloadPDF}>Download PDF</button>
            <div className="table-responsive">
                <table>
                    <thead>
                        <tr>
                            <th>Date</th>
                            <th>Description</th>
                            <th>Amount</th>
                            <th>Type</th>
                        </tr>
                    </thead>
                    <tbody>
                        {transactions.map(transaction => (
                            <tr key={transaction._id}>
                                <td>{new Date(transaction.createdAt).toLocaleDateString()}</td>
                                <td>{transaction.description || transaction.title}</td>
                                <td>{transaction.amount}</td>
                                <td>{transaction.amount > 0 ? 'Income' : 'Expense'}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </TransactionViewStyled>
    );
};

const TransactionViewStyled = styled.div`
    margin: 2rem 0;

    h2 {
        margin-bottom: 1rem;
        font-size: 1.8rem;
    }

    button {
        padding: 10px 15px;
        margin-bottom: 1rem;
        background-color: #007bff;
        color: white;
        border: none;
        border-radius: 5px;
        cursor: pointer;

        &:hover {
            background-color: #0056b3;
        }
    }

    .table-responsive {
        overflow-x: auto; /* Allows horizontal scrolling on small devices */
        margin-bottom: 2rem;

        table {
            width: 100%;
            border-collapse: collapse;

            th, td {
                padding: 12px;
                text-align: left;
                border-bottom: 1px solid #ddd;

                /* Responsive styling */
                @media (max-width: 768px) {
                    padding: 10px;
                    font-size: 0.9rem; /* Adjust font size */
                }
            }

            th {
                background-color: #f4f4f4;
            }

            tr:hover {
                background-color: #f1f1f1;
            }
        }
    }

    @media (max-width: 768px) {
        h2 {
            font-size: 1.5rem; /* Smaller heading on smaller screens */
        }
    }
`;

export default TransactionView;
