import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import bg from './img/bg.png';
import { MainLayout } from './styles/Layouts';
import { Toaster } from 'sonner';
import Navigation from './Components/Navigation/Navigation';
import Dashboard from './Components/Dashboard/Dashboard';
import Income from './Components/Income/Income';
import Expenses from './Components/Expenses/Expenses';
import TransactionView from './Components/transaction';
import { useGlobalContext } from './context/globalContext';
import { Routes, Route, Navigate, useNavigate, Link } from 'react-router-dom';
import Authentification from './Components/Auth/auth';

function App() {
    const [active, setActive] = useState(1);
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const navigate = useNavigate();

    const global = useGlobalContext();
    console.log(global);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsAuthenticated(true);
        } else {
            setIsAuthenticated(false);
        }
    }, []);

    const handleAuthentication = (authStatus, redirectPath = '/') => {
        setIsAuthenticated(authStatus);
        if (authStatus) {
            navigate(redirectPath);
        }
    };

    return (
        <AppStyled bg={bg} className="App">
            <Toaster richColors />
            <MainLayout>
                {isAuthenticated ? (
                    <>
                        <Navigation active={active} setActive={setActive} />
                        <main>
                            <nav>
                                <ul>
                                    <li><Link to="/">Dashboard</Link></li>
                                    <li><Link to="/income">Income</Link></li>
                                    <li><Link to="/expenses">Expenses</Link></li>
                                    <li><Link to="/transactions">View Transactions</Link></li> {/* Add the new link here */}
                                </ul>
                            </nav>
                            <Routes>
                                <Route path="/" element={<Dashboard />} />
                                <Route path="/income" element={<Income />} />
                                <Route path="/expenses" element={<Expenses />} />
                                <Route path="/transactions" element={<TransactionView />} /> {/* New route */}
                                <Route path="*" element={<Navigate to="/" />} />
                            </Routes>
                        </main>
                    </>
                ) : (
                    <Routes>
                        <Route path="*" element={<Authentification onAuthenticate={handleAuthentication} />} />
                    </Routes>
                )}
            </MainLayout>
        </AppStyled>
    );
}

const AppStyled = styled.div`
    height: 100vh;
    background-image: url(${(props) => props.bg});

    main {
        flex: 1;
        background: rgba(252, 246, 249, 0.78);
        border: 3px solid #FFFFFF;
        backdrop-filter: blur(4.5px);
        border-radius: 32px;
        overflow-x: hidden;
        &::-webkit-scrollbar {
            width: 0;
        }
    }
    nav {
        padding: 1rem;
        ul {
            display: flex;
            justify-content: space-around;
            list-style-type: none;
            li {
                a {
                    text-decoration: none;
                    color: #222260;
                    font-weight: 500;
                    &:hover {
                        color: #007bff;
                    }
                }
            }
        }
    }
`;

export default App;
