import React, { useState } from 'react';
import { Login } from './login';
import { Registration } from './registration';

export default function Authentification() {
  const [currentForm, setCurrentForm] = useState('login');

  const toggleForm = (formName) => {
    setCurrentForm(formName);
  };

  const handleAuthentication = (authStatus) => {
    // Here you can set authentication status, e.g., update state in App.js
    console.log("Authentication status:", authStatus);
    // If you want to do more like redirect or change state, do that here
  };

  return (
    <div className="App">
      {
        currentForm === "login" 
          ? <Login onAuthenticate={handleAuthentication} onFormSwitch={toggleForm} /> 
          : <Registration onAuthenticate={handleAuthentication} onFormSwitch={toggleForm} />
      }
    </div>
  );
}
