import React, { useState, useMemo, useEffect } from 'react';
import { Toaster, toast } from 'sonner';
import { Routes, Route, Navigate, useNavigate, Link } from 'react-router-dom';
import { MainLayout } from './styles/Layouts';
import Orb from './Components/Orb/Orb';
import Navigation from './Components/Navigation/Navigation';
import Dashboard from './Components/Dashboard/Dashboard';
import Income from './Components/Income/Income';
import Expenses from './Components/Expenses/Expenses';
import { useGlobalContext } from './context/globalContext';
import Authentification from './Components/Auth/auth';

function App() {
    const [active, setActive] = useState(1);
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const navigate = useNavigate();

    const global = useGlobalContext();
    console.log(global);

    const orbMemo = useMemo(() => <Orb />, []);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsAuthenticated(true);
        } else {
            setIsAuthenticated(false);
        }
    }, []);

    const handleAuthentication = (authStatus, redirectPath = '/') => {
        setIsAuthenticated(authStatus);
        if (authStatus) {
            toast.success('Successfully logged in!');
            navigate(redirectPath);
        }
    };

    return (
        <div className="App bg-cover min-h-screen" style={{ backgroundImage: `url(${bg})` }}>
            {/* Place Toaster outside of Routes */}
            <Toaster richColors position="top-right" />

            {orbMemo}
            <MainLayout>
                {isAuthenticated ? (
                    <>
                        <Navigation active={active} setActive={setActive} />
                        <main className="flex-1 bg-[rgba(252,246,249,0.78)] border-3 border-white backdrop-blur rounded-3xl overflow-x-hidden">
                            <nav className="p-4">
                                <ul className="flex justify-around list-none">
                                    <li>
                                        <Link to="/" className="no-underline text-[#222260] font-medium hover:text-blue-500">
                                            Dashboard
                                        </Link>
                                    </li>
                                    <li>
                                        <Link to="/income" className="no-underline text-[#222260] font-medium hover:text-blue-500">
                                            Income
                                        </Link>
                                    </li>
                                    <li>
                                        <Link to="/expenses" className="no-underline text-[#222260] font-medium hover:text-blue-500">
                                            Expenses
                                        </Link>
                                    </li>
                                </ul>
                            </nav>
                            <Routes>
                                <Route path="/" element={<Dashboard />} />
                                <Route path="/income" element={<Income />} />
                                <Route path="/expenses" element={<Expenses />} />
                                <Route path="*" element={<Navigate to="/" />} />
                            </Routes>
                        </main>
                    </>
                ) : (
                    <Routes>
                        <Route 
                            path="*" 
                            element={<Authentification onAuthenticate={handleAuthentication} />} 
                        />
                    </Routes>
                )}
            </MainLayout>
        </div>
    );
}

export default App;
