import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { InnerLayout } from '../../styles/Layouts';
import Chart from '../Chart/Chart';
import TransactionList from '../transaction';

function Dashboard() {
    const { totalExpenses, totalIncome, totalBalance, getIncomes, getExpenses } = useGlobalContext();

    useEffect(() => {
        getIncomes();
        getExpenses();
    }, [getIncomes, getExpenses]); // Add dependencies for safety

    const formatNumber = (number) => {
        return new Intl.NumberFormat('fr-CM', {
            style: 'currency',
            currency: 'XAF',
            minimumFractionDigits: 0,
            maximumFractionDigits: 0
        }).format(number);
    };

    return (
        <DashboardStyled>
            <InnerLayout>
                <h1>All Transactions</h1>
                <div className="stats-con">
                    <div className="chart-con">
                        <Chart />
                        <div className="amount-con">
                            <div className="income">
                                <h2>Total Income</h2>
                                <p>{formatNumber(totalIncome())}</p>
                            </div>
                            <div className="expense">
                                <h2>Total Expense</h2>
                                <p>{formatNumber(totalExpenses())}</p>
                            </div>
                            <div className="balance">
                                <h2>Total Balance</h2>
                                <p>{formatNumber(totalBalance())}</p>
                            </div>
                        </div>
                    </div>
                    <div className="history-con">
                        <History />
                    </div>
                </div>
                <TransactionList /> {/* Add the TransactionList component here */}
            </InnerLayout>
        </DashboardStyled>
    );
}

const DashboardStyled = styled.div`
    .stats-con {
        display: grid;
        grid-template-columns: repeat(5, 1fr);
        gap: 2rem;

        @media (max-width: 1200px) {
            grid-template-columns: 1fr 1fr;
        }

        @media (max-width: 768px) {
            grid-template-columns: 1fr;
        }

        .chart-con {
            grid-column: 1 / 4;
            height: 400px;

            @media (max-width: 1200px) {
                grid-column: 1 / -1;
            }

            .amount-con {
                display: grid;
                grid-template-columns: repeat(3, 1fr);
                gap: 2rem;
                margin-top: 2rem;

                @media (max-width: 1200px) {
                    grid-template-columns: 1fr;
                    gap: 1.5rem;
                }

                @media (max-width: 768px) {
                    grid-template-columns: 1fr;
                    gap: 1rem;
                }

                .income, .expense, .balance {
                    background: #FCF6F9;
                    border: 2px solid #FFFFFF;
                    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                    border-radius: 20px;
                    padding: 1rem;
                    display: flex;
                    flex-direction: column;
                    justify-content: space-between;
                    height: 100%;

                    h2 {
                        font-size: 1.5rem;
                        margin-bottom: 0.5rem;
                    }

                    p {
                        font-size: 1.8rem;
                        font-weight: 700;
                        word-break: break-word;
                    }
                }

                .balance p {
                    color: var(--color-green);
                    opacity: 0.6;
                }
            }
        }
    }
`;

export default Dashboard;
